import { GET_ERROR} from '../actions/types'

const initialState = {
    msg: {},
    type: null,
}

export default function  alertReducer(state = initialState , action) {
    switch (action.type) {
        case GET_ERROR:
            return {
                msg: action.payload.msg,
                type: action.payload.type,

            }
            
        default: 
            return state;
    }

}
