import React from 'react'

const menu = [{
    name: 'General', href: '#', children: [
        { name: 'Dashboard', path: '/', icon: 1, current: true },
        { name: 'Employee', path: '/employee', icon: 1, current: false },
        { name: 'Department', path: '/department', icon: 1, current: false },
        { name: 'Timesheet', path: '/timesheet', icon: 1, current: false },
    ]
},
{
    name: 'Customization', href: '#', children: [
        { name: 'Approval', path: '/approval', icon: 1, current: false },
        { name: 'Salary', path: '/department', icon: 1, current: false },
        { name: 'Statistic', path: '/statistic', icon: 1, current: false },
    ]
},
{
    name: 'Configuration', href: '#', children: [
        { name: 'Account', path: '/', icon: 1, current: true },
    ]
},
]

function Sidebar() {
    return (
        <div className="w-60 h-screen bg-slate-800 text-white fixed shadow">
            <div className="flex items-center p-3 ">
                <img src="https://joeschmoe.io/api/v1/random"
                    className="w-12 h-12 rounded-full object-cover object-center border-2"
                    alt="avt" />
                <div className="mx-1">
                    <div className="font-medium">user</div>
                    <div>
                        <span className="inline-flex rounded-full h-3 w-3 bg-green-500"></span>
                        <span className="text-sm mx-1">Online</span>
                    </div>
                </div>
            </div>
            <div>
                {menu.map((items, index) => {
                    return <div key={index}>
                        <div className="bg-slate-700 p-1 uppercase"> {items.name}</div>
                        <ul className="">
                            {items.children.map((item, index) => {
                                return <li key={index}>
                                    <span className="mx-1">{item.icon}</span>
                                    <span>{item.name}</span>
                                </li>
                            })}
                        </ul>
                    </div>
                })}

            </div>
        </div>
    )
}

export default Sidebar
