import { SET_EMPLOYEES } from "./types";
import employeeApi from "../../api/employeeApi";
//GET_LIST
export const setEmployee = (item) => ({
  type: SET_EMPLOYEES,
});
export const fetchEmployees = () => async (dispatch) => {
  const res = await employeeApi.getAll();
  dispatch({
    type: GET_EMPLOYEES,
    payload: res,
  });
};
