import { SET_EMPLOYEES,DELETE_EMPLOYEE, ADD_EMPLOYEE, GET_ALERT} from "./types";
import employeeApi from "../../api/employeeApi";
import { useNavigate } from "react-router-dom";
//getAll list Employee
export const setEmployee = (items) => ({
  type: SET_EMPLOYEES,
  payload: items,
});
// fetch all Employee
export const fetchEmployees = () => async (dispatch) => {
  employeeApi.getAll()
  .then(res =>  dispatch(setEmployee(res)))
  .catch(err => console.log(err))
};
// Delete employee
export const deleteEmployee = (id) => (dispatch) => {
  employeeApi.delete(id)
  .then( res =>
    dispatch({
      type: DELETE_EMPLOYEE,
      payload: id,
    })
  )
  .catch(err => console.log(err));
}

// add employee
export const addEmployee = (employee) => (dispatch) => {

  employeeApi.post(employee)
  .then( res => {
    let navigate = useNavigate();
    dispatch({
        type: GET_ALERT,
        payload:{
          msg: `Success create employee id ${res.id}`,
          type: 'success',
        }
      })
    dispatch({
      type: ADD_EMPLOYEE,
      payload: res
    });
    navigate("../employee", { replace: true })
  })
  .catch(err => {
    console.log(err.response)
    const errMsg = {
      msg: err.response.statusText,
      type: 'error',
    }
    dispatch({ type: GET_ALERT, payload: errMsg})
  });
}
