import { GET_EMPLOYEES } from "./types";
import employeeApi from "../../api/employeeApi";
//GET_LIST
export const getEmployees = () => async (dispatch) => {
  try {
    const res = await employeeApi.getAll();
    dispatch({
      type: GET_EMPLOYEES,
      payload: res,
    });
  } catch (e) {
    console.log("Failed to fetch employee list: ", e);
  }
};
