const  initialState = {
    somethings: "department",
    list: [],
  };

export default function departmentReducer(state = initialState, action) {
    switch (action.type) {
        case 1:
            return {
                msg: action.payload.msg,
                status: action.payload.status,
                id: action.payload.id,
            }
        case CLEAR_ERROR:
            return {
               msg: {},
               status: null,
               id: null, 
            }
        default:
            return state
    }
}
