import { SET_EMPLOYEES, DELETE_EMPLOYEE } from "../actions/types.js";

const initalState = {
  somethings: "text",
  list: [],
};

export default function employeeReducer(state = initalState, action) {
  switch (action.type) {
    case SET_EMPLOYEES:
      const newList = [...action.payload]
      return {
        ...state,
        list: action.payload,
      };
    case DELETE_EMPLOYEE: 
      return {
        ...state,
        list: state.employee.list.filter(emp => emp.id !== action.id)
      }
    default:
      return state;
  }
}
