import {useState} from 'react'
import { UilAngleLeft,UilAngleRight } from '@iconscout/react-unicons'
function Pagination({countPerPage, totalPages}) {
    const pageNumber = []
    for(let i = 1; i <= Math.ceil(totalPages / countPerPage); i++) {
            pageNumber.push(i)
    }
    return (
        <div className="w-fit">
            <ul className="flex divide-x border">
                <li key="0">
                    <button className="py-2 px-3  text-violet-600">
                           Previous
                    </button>
                </li>
                {pageNumber.map(number => {
                    return(
                    <li key={number}>
                        <button className="py-2 px-3  text-violet-600">
                            {number}
                        </button>
                    </li>)
                })}
                <li key={pageNumber.length + 1}>
                    <button className="py-2 px-3  text-violet-600">
                         Next
                    </button>
                </li>
            </ul>
        </div>
    )
 
}

export default Pagination
