import {useState} from 'react'

function Pagination({countPerPage, totalPages}) {
    const pageNumber = []
    for(let i = 1; i <= Math.ceil(totalPages / countPerPage); i++) {
            pageNumber.push(i)
    }
    return (
        <div>
            <ul className="flex divide-x">
                {pageNumber.map(number => {
                    return(
                    <li key={number}>
                        <button className="py-2 px-3  text-violet-600">
                            {number}
                        </button>
                    </li>)
                })}
            </ul>
        </div>
    )
 
}

export default Pagination
