import React from 'react'

function Pagination({countPerPage, totalPages}) {
    const pageNumber = []
    for(let i = 1; i <= Math.ceil(totalPages / countPerPage); i++) {
        pageNumber.push(i)
    }
    console.log(pageNumber)
    return (
    <ul>
        {pageNumber[0] && pageNumber.map(number => {
            <li key={number}>
                <button href="!#" className="p-3 border text-violet-600">
                    {number}
                </button>
            </li>
        })}
    </ul>
  )
}

export default Pagination
