import { GET_ERROR} from '../actions/types'

const initialState = {
    msg: {},
    status: null
}

export default function  errorReducer(state = initialState , action) {
    switch (action.type) {
        case GET_ERROR:
            return{
                msn: action.payload.msg,
                state: action.payload.status
            }
        default: 
            return state;
    }

}
