import { GET_ERROR} from '../actions/types'

const initialState = {
    msg: {},
    status: null,
    type: null,
}

export default function  alertReducer(state = initialState , action) {
    switch (action.type) {
        case GET_ERROR:
            return{
                msn: action.payload.msg,
                state: action.payload.status,
                type: 'error',
            }
        default: 
            return state;
    }

}
