// import { useState } from 'react'
import { UilAngleLeft,UilAngleRight } from '@iconscout/react-unicons'


function Pagination({amountPerPage,currentPage, totalPages, paginate, amountItem}) {
    // const [num,setNum] = useState(1)
    const pageNumber = []
    for(let i = 1; i <= Math.ceil(totalPages / amountPerPage); i++) {
            pageNumber.push(i)
    }
    // console.log(num)
    const handlePrevious = () => {
        paginate(currentPage-1);
    }
    return (
        <div className="flex item-content">
           
            <select onChange={(e)=> amountItem(parseInt(e.target.value))} className="mr-2 border border-violet-500 py-1.5 px-2 bg-gray-50 font-medium text-violet-600 focus:outline-none">
                <option value="10">10 / page </option>
                <option value="15">15 / page</option>
                <option value="25">25 / page</option>
            </select>
        
            <ul className="flex divide-x border border-violet-500 items-center w-fit">
                <li key="0">
                    <button disabled={false} onClick={handlePrevious} className={`${ false ? "text-gray-400": "hover:bg-gray-300 text-violet-500"}  py-1.5 px-2  `}>
                           <UilAngleLeft/>
                    </button>
                </li>
                {pageNumber.map(number => {
                    return(
                    <li key={number}>
                        <button onClick={()=> {setNum(number); paginate(number)}} className="hover:bg-gray-300 py-1.5 px-3  text-violet-500">
                            {number}
                        </button>
                    </li>)
                })}
                <li key={pageNumber.length + 1}>
                    <button disabled={true}  className="hover:bg-gray-300 py-1.5 px-2  text-violet-500">
                         <UilAngleRight/>
                    </button>
                </li>
            </ul>
        </div>
    )
 
}

export default Pagination
