import { UilAngleLeft,UilAngleRight } from '@iconscout/react-unicons'

function Pagination({countPerPage, totalPages, paginate}) {
    const pageNumber = []
    for(let i = 1; i <= Math.ceil(totalPages / countPerPage); i++) {
            pageNumber.push(i)
    }
    return (
        <div className="">
            <ul className="flex divide-x border items-center w-fit">
                <li key="0">
                    <button className="py-2 px-2  text-violet-600">
                           <UilAngleLeft/>
                    </button>
                </li>
                {pageNumber.map(number => {
                    return(
                    <li key={number}>
                        <button onClick={()=> paginate(number)} className="py-2 px-3  text-violet-600">
                            {number}
                        </button>
                    </li>)
                })}
                <li key={pageNumber.length + 1}>
                    <button className="py-2 px-2  text-violet-600">
                         <UilAngleRight/>
                    </button>
                </li>
            </ul>
        </div>
    )
 
}

export default Pagination
